mapper.select:
select pc.*,cucu.user_id,cucu.customer_id,cucu.card_number from vcc_payment_control pc


        INNER JOIN


        LEFT JOIN


(
    select cuc.*,u.customer_id from(
        select c.*,uc.user_id from vcc_card c LEFT JOIN vcc_user_card uc on uc.card_id = c.id
        where 1 = 1

            and uc.user_id = #{user_id}


            and c.card_number = #{card_number}

    )cuc LEFT JOIN sys_user u on u.user_id = cuc.user_id
)cucu on cucu.purchase_request_id = pc.purchaseRequestID
where 1 = 1

    and pc.purchaseRequestId like '%${purchase_request_id}%'


    and pc.merchantName like '%${merchant_name}%'


    and pc.billingAmount = #{billing_amount}


    and pc.vcnStatus = #{vcn_status}


    and pc.transactionType = #{transaction_type}


    and pc.transactionDate BETWEEN #{startTime} and #{endTime}

order by pc.transactionDate desc
limit #{page}, #{rows}

select count(*) from(
    select pc.*,cucu.user_id,cucu.customer_id from vcc_payment_control pc


            INNER JOIN


            LEFT JOIN


    (
    select cuc.*,u.customer_id from(
            select c.*,uc.user_id from vcc_card c LEFT JOIN vcc_user_card uc on uc.card_id = c.id
            where 1 = 1

                and uc.user_id = #{user_id}


                and c.card_number = #{card_number}

        )cuc LEFT JOIN sys_user u on u.user_id = cuc.user_id
    )cucu on cucu.purchase_request_id = pc.purchaseRequestID
    where 1 = 1

        and pc.purchaseRequestId like '%${purchase_request_id}%'


        and pc.merchantName like '%${merchant_name}%'


        and pc.billingAmount = #{billing_amount}



        and pc.vcnStatus = #{vcn_status}


        and pc.transactionType = #{transaction_type}


        and pc.transactionDate BETWEEN #{startTime} and #{endTime}

)cucpc

select * from(
    select uc.* from sys_user u left JOIN
    (
      select c.*,uc.user_id from vcc_card c LEFT JOIN vcc_user_card uc on uc.card_id = c.id
        where 1 = 1

            and uc.user_id = #{user_id}

    )uc
    on uc.user_id = u.user_id
)ucu inner JOIN vcc_payment_control pc on pc.purchaseRequestID = ucu.purchase_request_id
where 1 = 1

    and pc.purchaseRequestId like '%${purchase_request_id}%'


    and pc.merchantName like '%${merchant_name}%'


    and pc.billingAmount = #{billing_amount}



    and pc.vcnStatus = #{vcn_status}


    and pc.transactionType = #{transaction_type}


    and pc.transactionDate BETWEEN #{startTime} and #{endTime}


    and pc.transactionType != '进货退回'

order by pc.transactionDate desc
limit #{page}, #{rows}

select count(*) from(
    select ucu.* from(
        select uc.* from sys_user u left JOIN
        (
        select c.*,uc.user_id from vcc_card c LEFT JOIN vcc_user_card uc on uc.card_id = c.id
        where 1 = 1

            and uc.user_id = #{user_id}

        )uc
        on uc.user_id = u.user_id
    )ucu inner JOIN vcc_payment_control pc on pc.purchaseRequestID = ucu.purchase_request_id
    where 1 = 1

        and pc.purchaseRequestId like '%${purchase_request_id}%'


        and pc.merchantName like '%${merchant_name}%'


        and pc.billingAmount = #{billing_amount}



        and pc.vcnStatus = #{vcn_status}


        and pc.transactionType = #{transaction_type}


        and pc.transactionDate BETWEEN #{startTime} and #{endTime}


        and pc.transactionType != '进货退回'

)pc
